WITH customer_consumption AS (SELECT T1.`CustomerID`, T1.`Segment`, SUM(T2.`Consumption`) AS total_consumption FROM customers AS T1 INNER JOIN yearmonth AS T2 ON T1.`CustomerID` = T2.`CustomerID` WHERE T1.`Currency` = 'CZK' AND T2.`Date` BETWEEN '201301' AND '201312' GROUP BY T1.`CustomerID`, T1.`Segment`), min_consumption_per_segment AS (SELECT `Segment`, MIN(total_consumption) AS min_total FROM customer_consumption GROUP BY `Segment`), lowest_customers AS (SELECT cc.`CustomerID`, cc.`Segment` FROM customer_consumption cc INNER JOIN min_consumption_per_segment mcps ON cc.`Segment` = mcps.`Segment` AND cc.total_consumption = mcps.min_total), avg_consumption_per_segment AS (SELECT lc.`Segment`, AVG(cc.total_consumption) AS avg_consumption FROM lowest_customers lc INNER JOIN customer_consumption cc ON lc.`CustomerID` = cc.`CustomerID` GROUP BY lc.`Segment`) SELECT (SELECT avg_consumption FROM avg_consumption_per_segment WHERE `Segment` = 'SME') - (SELECT avg_consumption FROM avg_consumption_per_segment WHERE `Segment` = 'LAM') AS SME_LAM_difference, (SELECT avg_consumption FROM avg_consumption_per_segment WHERE `Segment` = 'LAM') - (SELECT avg_consumption FROM avg_consumption_per_segment WHERE `Segment` = 'KAM') AS LAM_KAM_difference, (SELECT avg_consumption FROM avg_consumption_per_segment WHERE `Segment` = 'KAM') - (SELECT avg_consumption FROM avg_consumption_per_segment WHERE `Segment` = 'SME') AS KAM_SME_difference;
WITH consumption_by_segment AS (SELECT c.Segment, SUM(CASE WHEN SUBSTR(ym.Date, 1, 4) = '2012' THEN ym.Consumption ELSE 0 END) AS consumption_2012, SUM(CASE WHEN SUBSTR(ym.Date, 1, 4) = '2013' THEN ym.Consumption ELSE 0 END) AS consumption_2013 FROM yearmonth ym JOIN customers c ON ym.CustomerID = c.CustomerID WHERE c.Currency = 'EUR' AND SUBSTR(ym.Date, 1, 4) IN ('2012', '2013') GROUP BY c.Segment), percentage_changes AS (SELECT Segment, ((consumption_2013 - consumption_2012) / consumption_2012) * 100 AS percentage_increase FROM consumption_by_segment WHERE consumption_2012 > 0 AND consumption_2013 > 0) SELECT Segment FROM percentage_changes WHERE percentage_increase = (SELECT MAX(percentage_increase) FROM percentage_changes) UNION ALL SELECT Segment FROM percentage_changes WHERE percentage_increase = (SELECT MIN(percentage_increase) FROM percentage_changes);
